# This is the procedure to create the logo. 
# After running this procedure we end up with exactly what is stored in the `LOGO` constant.
# It is meant to be used in case the logo needs to updated.

update.logo <- function(ascii.logo.path = "./assets/logo/logo", color = TRUE) {
    # Load the ASCII logo.
    logo <- readLines(ascii.logo.path)
    logo <- dput(logo)

    # Should the logo have colors?
    if(color) {
        # Add green color for "Sample size" and "Recommendations".
        logo <- gsub("(Sample size|Recommendations)", "\033[32m\\1\033[39m" , logo, perl = TRUE)

        # Add silver color for "v1.0.0".
        logo <- gsub("(v1.0.0)", "\033[90m\\1\033[39m" , logo, perl = TRUE)

        # Add silver color for certain ".".
        logo <- gsub("(\\.(?!\\d|_|(\\s\\()|([a-z])))", "\033[90m\\1\033[39m" , logo, perl = TRUE)

        # Underline the email address.
        logo <- gsub("(m.a.constantin@uvt.nl)", "\033[4m\\1\033[24m" , logo, perl = TRUE)
    }

    # Print the logo.
    cat(logo, sep = "\n")

    # Condensed version.
    logo <- paste(logo, collapse = "\n")

    return(logo)
}



